package VariableAndDatatype;

public class Arithmaticoperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a=10;
		int b=20;
		//+ operator
		
		System.out.println(a+b);
		System.out.println(b-a);
		System.out.println(a*b);
		System.out.println(b/a);
		
		//%
		System.out.println(b%a);
		

	}

}
